import { useState } from 'react';
import { eyeOpen, togglePasswordVisibility } from '../lib/password-visibility';

const jsonifyForm = (form) => {
  const formKeyNodes = form.querySelectorAll(
    '.field > :nth-child(1):not(.submit)'
  );
  const formValNodes = form.querySelectorAll('.field > :nth-child(2)');

  const jsonifiedForm = {};
  for (var i = 0; i < formKeyNodes.length; i++)
    jsonifiedForm[formKeyNodes[i].textContent] = formValNodes[i].value;

  return jsonifiedForm;
};

const onfulfilled = (response) => {
  if (response.status === 400) {
    return;
  } else {
    const card = document.querySelector('#register > div.card');
    card.querySelectorAll('*').forEach((elem) => elem.remove());
    const h2 = document.createElement('h2');
    card.appendChild(h2);
    h2.textContent = 'Your account was successfully created!';
  }
};

const onsubmitHandler = (e) => {
  e.preventDefault();

  const form = e.target;
  const formData = jsonifyForm(form);

  const postReq = {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(formData),
  };

  fetch('https://weak-puce-toad-garb.cyclic.app/', postReq)
    .then(onfulfilled)
    .catch((error) => console.log(error));
};

const EyeIcon = (props) => {
  return (
    <svg
      className='eye-icon'
      onClick={props.eyeClickHandler}
      width='113'
      height='66'
      viewBox='0 0 113 66'
      fill='none'
      xmlns='http://www.w3.org/2000/svg'
    >
      <path d={props.svgPath} stroke='black' strokeWidth='5' />
      <circle cx={props.svgCX} cy='33.5' r='18.5' fill='black' />
      <line
        x1='1.07793'
        y1='3.74433'
        x2='114.078'
        y2='57.7443'
        stroke='black'
        strokeWidth={props.lineWidth}
      />
    </svg>
  );
};

const Register = () => {
  const [svgPath, setSVGPath] = useState(eyeOpen);
  const [svgCX, setSVGcx] = useState('56.5');
  const [lineWidth, setLineWidth] = useState('0');

  const [passVis, setPassVis] = useState('password');

  const eyeClickHandler = () =>
    togglePasswordVisibility(
      [passVis, setPassVis],
      [svgPath, setSVGPath],
      [svgCX, setSVGcx],
      [lineWidth, setLineWidth]
    );

  return (
    <body id='register'>
      <div className='card'>
        <h3>Create an account</h3>
        <form onSubmit={onsubmitHandler}>
          <div className='field'>
            <div>Email</div>
            <input name='Email' type='email' />
          </div>
          <div className='name-section'>
            <div className='field'>
              <div>First name</div>
              <input name='First name' type='text' />
            </div>
            <div className='field'>
              <div>Last name</div>
              <input name='Last name' type='text' />
            </div>
          </div>
          <div className='password-section'>
            <div className='field'>
              <div>Password</div>
              <input name='Password' type={passVis} />
              <EyeIcon
                eyeClickHandler={eyeClickHandler}
                svgPath={svgPath}
                svgCX={svgCX}
                lineWidth={lineWidth}
              />
            </div>
            <div className='field'>
              <div>Confirm new password</div>
              <input name='Confirm new password' type={passVis} />
              <svg
                className='eye-icon'
                onClick={eyeClickHandler}
                width='113'
                height='66'
                viewBox='0 0 113 66'
                fill='none'
                xmlns='http://www.w3.org/2000/svg'
              >
                <path d={svgPath} stroke='black' strokeWidth='5' />
                <circle cx={svgCX} cy='33.5' r='18.5' fill='black' />
                <line
                  x1='1.07793'
                  y1='3.74433'
                  x2='114.078'
                  y2='57.7443'
                  stroke='black'
                  strokeWidth={lineWidth}
                />
              </svg>
            </div>
          </div>
          <div className='field'>
            <input
              className='submit'
              type='submit'
              name='Create account'
              value='Create account'
            />
          </div>
          <p style={{ fontSize: '10pt' }}>
            Already have an account? <a href='login'>Sign in.</a>
          </p>
        </form>
      </div>
    </body>
  );
};

export default Register;
