import { useState } from 'react';

const sendOpenAIPrompt = (description) => {
  fetch('https://weak-puce-toad-garb.cyclic.app', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ description: description }),
  });
};

const Sell = () => {
  const [description, setDescription] = useState('');
  return (
    <body id='sell'>
      <h2>What do you have to sell?</h2>
      <input
        value={description}
        onChange={(e) => {
          setDescription(e.target.value);
        }}
      />
      <button className='submit' onClick={sendOpenAIPrompt(description)}>
        Sell
      </button>
    </body>
  );
};

export default Sell;
