import Quote from '../components/Quote';

const Intro = () => {
  return (
    <body id='intro'>
      <div>
        <p>
          Mainstream philosophy of religion has fallen into disrepute, even
          among its own practitioners. The discipline sorely needs
          reconstruction and reinvigorationâ€”of the sort Willard Van Orman Quine
          famously spearheaded in metaphysics, a field which was in Quine's day
          lambasted by an influential cadre of philosophers and scientists known
          as the Vienna Circle. One of Quine's most germane theses is:
        </p>
        <Quote
          text={
            '[P]hilosophy . . . as an effort to get clearer on things, is not to be distinguished in essential points of purpose and method from good and bad science.'
          }
        />
        <p></p>
      </div>
    </body>
  );
};

export default Intro;
