import { useRef, forwardRef, useState } from 'react';

const Overlay = forwardRef((props, ref) => {
  return <div id='overlay' ref={ref}></div>;
});

const MenuIcon = (props) => {
  const [xified, setXified] = useState(false);
  const [clickedOnce, setClickedOnce] = useState(false);
  const opacifiables = [props.menuRef, props.overlayRef, props.loginBarRef];

  return (
    <div
      id='menu-icon'
      onClick={() => {
        setXified(!xified);
        for (let elem of opacifiables) {
          if (elem) {
            elem.current.classList.add(xified ? 'hidden' : 'shown');
            elem.current.classList.remove(xified ? 'shown' : 'hidden');

            var items;
            const isMenu = elem === props.menuRef;
            const isLoginBar = elem === props.loginBarRef;
            if (isMenu || isLoginBar)
              items = elem.current.querySelectorAll(
                isMenu ? 'a' : '#login-bar > a'
              );

            if (items)
              if (xified) {
                for (let item of items) item.classList.remove('active');
              } else {
                for (let item of items) item.classList.add('active');
              }
          }
        }
        if (props.menuRef) {
          const menuItems = props.menuRef.current.querySelectorAll('a');
          if (xified) {
            props.menuRef.current.classList.add('hidden');
            props.menuRef.current.classList.remove('shown');
            for (let item of menuItems) item.classList.remove('active');
          } else {
            props.menuRef.current.classList.remove('hidden');
            props.menuRef.current.classList.add('shown');
            for (let item of menuItems) item.classList.add('active');
          }
        }
        if (props.overlayRef) {
          if (xified) {
            props.overlayRef.current.classList.add('hidden');
            props.overlayRef.current.classList.remove('shown');
          } else {
            props.overlayRef.current.classList.remove('hidden');
            props.overlayRef.current.classList.add('shown');
          }
        }
        if (props.loginBarRef) {
          const loginItems =
            props.loginBarRef.current.querySelectorAll('#login-bar > a');
          if (xified) {
            props.loginBarRef.current.classList.add('hidden');
            props.loginBarRef.current.classList.remove('shown');
            for (let item of loginItems) item.classList.remove('active');
          } else {
            props.loginBarRef.current.classList.remove('hidden');
            props.loginBarRef.current.classList.add('shown');
            for (let item of loginItems) item.classList.add('active');
          }
        }
        if (!clickedOnce) setClickedOnce(true);
      }}
      className={xified ? 'x-ified' : clickedOnce ? 'de-x-ified' : ''}
    >
      <div style={{ cursor: 'pointer' }}></div>
      <div></div>
      <div style={{ cursor: 'pointer' }}></div>
    </div>
  );
};

const MenuBar = (props) => {
  const overlayRef = useRef(null);
  return (
    <div id='menu-bar'>
      <Overlay ref={overlayRef} />
      <MenuIcon
        menuRef={props.menuRef}
        overlayRef={overlayRef}
        loginBarRef={props.loginBarRef}
      />
    </div>
  );
};

const Menu = forwardRef((props, ref) => {
  return (
    <div id='menu' ref={ref}>
      <div>
        <a href='catalog'>Catalog</a>
      </div>
      <div>
        <a href='pricing'>Pricing</a>
      </div>
    </div>
  );
});

const LoginBar = forwardRef((props, ref) => {
  return (
    <div id='login-bar' ref={ref}>
      <a href='login'>
        <button>Sign in</button>
      </a>
      <a href='start'>
        <button>Start free trial</button>
      </a>
    </div>
  );
});

const Home = () => {
  const menuRef = useRef(null);
  const loginBarRef = useRef(null);
  return (
    <div>
      <MenuBar menuRef={menuRef} loginBarRef={loginBarRef} />
      <Menu ref={menuRef} />
      <LoginBar ref={loginBarRef} />
    </div>
  );
};

export default Home;
