import { useRef, forwardRef, useState } from 'react';

const Register = () => {
  const formRef = useRef(null);

  const submitForm = async () => {
    const registrationForm = formRef.current;
    const form = jsonifyForm(registrationForm);
    // const postReq = {
    //   method: 'POST',
    //   headers: {
    //     Accept: '*/*',
    //     'Accept-Encoding': 'gzip, deflate, br',
    //     'Content-Length': '147',
    //     'Content-Type': 'application/json',
    //     Host: 'weak-puce-toad-garb.cyclic.app',
    //     'User-Agent': 'Thunder Client (https://www.thunderclient.com)',
    //     // 'X-Forwarded-For': '99.65.186.42',
    //     // 'X-Forwarded-Port': '443',
    //     // 'X-Forwarded-Proto': 'https',
    //   },
    //   body: JSON.stringify(form),
    // };

    // prettier-ignore
    const postReq = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: form,
    };
    const res = await fetch(
      `https://weak-puce-toad-garb.cyclic.app/`,
      postReq
    ).catch((error) => console.log(error));
    console.log(res);
    // pantryClient.basket
    //   .update('accounts', form, options)
    //   .then((response) => console.log(response));
  };

  const jsonifyForm = (form) => {
    const formKeyNodes = form.querySelectorAll(
      '.field > :nth-child(1):not(.submit)'
    );
    const formValNodes = form.querySelectorAll('.field > :nth-child(2)');

    const jsonifiedForm = {};
    for (var i = 0; i < formKeyNodes.length; i++)
      jsonifiedForm[formKeyNodes[i].textContent] = formValNodes[i].value;

    return jsonifiedForm;
  };

  return (
    <div className='card'>
      <h3>Create an account</h3>
      <form ref={formRef}>
        <div className='field'>
          <div>Email</div>
          <input type='email' />
        </div>
        <div className='name-section'>
          <div className='field'>
            <div>First name</div>
            <input type='text' />
          </div>
          <div className='field'>
            <div>Last name</div>
            <input type='text' />
          </div>
        </div>
        <div className='password-section'>
          <div className='field'>
            <div>Password</div>
            <input type='password' />
          </div>
          <div className='field'>
            <div>Confirm new password</div>
            <input type='password' />
          </div>
        </div>
        <div className='field'>
          <input
            className='submit'
            type='submit'
            value='Create account'
            onClick={submitForm}
          />
        </div>
        <p style={{ fontSize: '10pt' }}>
          Already have an account? <a href='login'>Sign in.</a>
        </p>
      </form>
    </div>
  );
};

export default Register;
