import { useState, useEffect } from 'react';

import EyeIcon from '../components/EyeIcon';
import { eyeOpen, togglePasswordVisibility } from '../lib/password-visibility';

import { onsubmitHandler } from '../lib/form-handling';

const onfulfilled = (nameOfUser) => {
  console.log(`Promise resolved\nresponse = ${nameOfUser}`);

  const loginBody = document.querySelector('#login');
  loginBody.querySelectorAll('*').forEach((elem) => elem.remove());
  const h2 = document.createElement('h2');
  h2.textContent = `Welcome to Litrit, ${nameOfUser}!`;
  const link = document.createElement('a');
  link.href = 'catalog';
  loginBody.appendChild(link);
  const btn = document.createElement('button');
  link.appendChild(btn);
  btn.textContent = 'View catalog';
};

const onrejected = (nameOfUser) => {
  console.log(`Promise rejected\nresponse = ${nameOfUser}`);
};

const Login = () => {
  useEffect(() => {
    document.title = 'Sign in';
  });

  const [svgPath, setSVGPath] = useState(eyeOpen);
  const [svgCX, setSVGcx] = useState('56.5');
  const [lineWidth, setLineWidth] = useState('0');

  const [passVis, setPassVis] = useState('password');

  const eyeClickHandler = () =>
    togglePasswordVisibility(
      [passVis, setPassVis],
      [svgPath, setSVGPath],
      [svgCX, setSVGcx],
      [lineWidth, setLineWidth]
    );

  return (
    <body id='login'>
      <div className='card'>
        <h3>Sign in</h3>
        <form
          onSubmit={(e) => onsubmitHandler(e, 'GET', onfulfilled, onrejected)}
        >
          <div className='field'>
            <div>Email</div>
            <input type='email' />
          </div>
          <div className='field'>
            <div>Password</div>
            <input type={passVis} />
            <EyeIcon
              eyeClickHandler={eyeClickHandler}
              svgPath={svgPath}
              svgCX={svgCX}
              lineWidth={lineWidth}
            />
          </div>
          <div className='field'>
            <input className='submit' type='submit' value='SIGN IN' />
          </div>
          <p style={{ fontSize: '10pt' }}>
            New to Litrit? <a href='register'>Create an account.</a>
          </p>
        </form>
      </div>
    </body>
  );
};

export default Login;
