import { useRef, forwardRef, useState } from 'react';

const Overlay = forwardRef((props, ref) => {
  return <div id='overlay' ref={ref}></div>;
});

const MenuIcon = (props) => {
  const [xified, setXified] = useState(false);
  const [clickedOnce, setClickedOnce] = useState(false);

  return (
    <div
      id='menu-icon'
      onClick={() => {
        setXified(!xified);
        if (props.menuRef) {
          if (xified) {
            props.menuRef.current.classList.add('hidden');
            props.menuRef.current.classList.remove('shown');
          } else {
            props.menuRef.current.classList.remove('hidden');
            props.menuRef.current.classList.add('shown');
          }
        }
        if (props.overlayRef) {
          if (xified) {
            props.overlayRef.current.classList.add('hidden');
            props.overlayRef.current.classList.remove('shown');
          } else {
            props.overlayRef.current.classList.remove('hidden');
            props.overlayRef.current.classList.add('shown');
          }
        }
        if (!clickedOnce) setClickedOnce(true);
      }}
      className={xified ? 'x-ified' : clickedOnce ? 'de-x-ified' : ''}
    >
      <div style={{ cursor: 'pointer' }}></div>
      <div></div>
      <div style={{ cursor: 'pointer' }}></div>
    </div>
  );
};

const MenuBar = (props) => {
  const overlayRef = useRef(null);
  return (
    <div id='menu-bar'>
      <Overlay ref={overlayRef} />
      <MenuIcon menuRef={props.menuRef} overlayRef={overlayRef} />
    </div>
  );
};

const Menu = forwardRef((props, ref) => {
  return (
    <div id='menu' ref={ref}>
      <div>
        <a href='catalog.html'>Catalog</a>
      </div>
      <div>
        <a href='pricing.html'>Pricing</a>
      </div>
    </div>
  );
});

const LoginBar = forwardRef((props, ref) => {
  return (
    <div id='login-bar' ref={ref}>
      <a href='login.html'>
        <button>Sign in</button>
      </a>
      <a href='start.html'>
        <button>Start free trial</button>
      </a>
    </div>
  );
});

const Home = () => {
  const menuRef = useRef(null);
  const loginBarRef = useRef(null);
  return (
    <div>
      <MenuBar menuRef={menuRef} loginBarRef={loginBarRef} />
      <Menu ref={menuRef} />
      <LoginBar />
    </div>
  );
};

export default Home;
